 Toast.makeText(
 requireContext(),
 "Permission caméra nécessaire pour scanner un QR code",
 Toast.LENGTH_LONG
 ).show()
 requestCameraPermissionLauncher.launch(Manifest.permission.CAMERA)
 }

 else -> {
 requestCameraPermissionLauncher.launch(Manifest.permission.CAMERA)
 }
 }
 }

 private fun startQrCodeScanner() {
 val integrator = IntentIntegrator.forSupportFragment(this)
 integrator.setDesiredBarcodeFormats(IntentIntegrator.QR_CODE)
 integrator.setPrompt("Scannez un QR Code")
 integrator.setCameraId(0) // 0 pour caméra arrière
 integrator.setBeepEnabled(false)
 integrator.setBarcodeImageEnabled(true)
 integrator.initiateScan()
 }

 override fun onActivityResult(requestCode: Int, resultCode: Int, data: Intent?) {
 val result = IntentIntegrator.parseActivityResult(requestCode, resultCode, data)
 if (result != null) {
 if (result.contents == null) {
 Toast.makeText(requireContext(), "Scan annulé", Toast.LENGTH_LONG).show()
 } else {
 try {
 // Supposons que le QR Code contient l'ID du produit sous forme de nombre entier
 val productId = result.contents.toInt()

 // Navigation vers la page de détails du produit
 val bundle = Bundle().apply {
 putInt("productId", productId)
 }
 findNavController().navigate(R.id.nav_product_detail, bundle)

 } catch (e: NumberFormatException) {
 Toast.makeText(
 requireContext(),
 "Format de QR Code invalide. Attendu: ID produit (nombre)",
 Toast.LENGTH_LONG
 ).show()
 }
 }
 } else {
 super.onActivityResult(requestCode, resultCode, data)
 }
 }

 override fun onDestroyView() {
 super.onDestroyView()
 _binding = null
 }
