 customSearchView.visibility = View.VISIBLE
 binding.searchView.visibility = View.GONE
 }
 }
 }

 // S'assurer que l'interface personnalisée est visible au début
 customSearchView.visibility = View.VISIBLE
 binding.searchView.visibility = View.GONE
 }

 private fun observeViewModel() {
